module.exports = {
  escape: (val) => {
    // Fake escape, returns quoted string
    return `'${String(val).replace(/'/g, "\\'")}'`;
  },
  format: (sql, values) => {
    // Very basic format substitute
    if (!Array.isArray(values)) return sql;
    return sql.replace(/\?/g, () => {
      const val = values.shift();
      return `'${String(val).replace(/'/g, "\\'")}'`;
    });
  }
};
